function toBuffer(hex) {
    return new HexString(hex).toUint8Array();
}

async function depoly() {
    const packageMetadata = fs.readFileSync(
        path.resolve(`contract_swap/build/${pkName}/package-metadata.bcs`)
    );
    const moduleData = fs.readFileSync(
        path.resolve(`contract_swap/build/${pkName}/bytecode_modules/${codeName}.mv`)
    );
    let tx = await client.publishPackage(
        account,
        new HexString(packageMetadata.toString("hex")).toUint8Array(),
        [new aptos.TxnBuilderTypes.Module(new HexString(moduleData.toString("hex")).toUint8Array())]
    );
    console.log("start depoly");
    await client.waitForTransaction(tx);
    await checkTxResult(tx);
}

