async function getCurrNonce(address) {
    let resources = await client.getAccountResources(address);
    fs.writeFileSync('data.json',JSON.stringify(resources, null, 4))
    const newStr = address.slice(2).replace(/^0+/, "");
    let k = `0x${newStr}`;
    let type = `${k}::evmcontract::S`;
    let defaultNonce = 1;
    let nonceResource = resources.find((r) => r.type === type);
    if (!nonceResource) return defaultNonce;
    return nonceResource.data.nonce.data.find((it) => it.key === k).value;
}

async function sendTx(payload) {
    const txnRequest = await client.generateTransaction(address, payload);
    const signedTxn = await client.signTransaction(account, txnRequest);
    const transactionRes = await client.submitTransaction(signedTxn);
    await client.waitForTransaction(transactionRes.hash);
    return transactionRes.hash;
}
start();

function toU256Hex(a, includePrefixe = true) {
    let it = toBeHex(a).slice(2).padStart(64, "0");
    if (includePrefixe) return "0x" + it;
    return it;
}
